class Address{CompleteAdress(n){var t=document.getElementById("Address_StateProvinceId"),i=t.options[t.selectedIndex].value;$(n).autocomplete({source:function(n,t){$.ajax({url:"/City/ListJson",type:"POST",dataType:"json",data:{id:$("#CityIdF").val(),terms:n.term,stateprovinceId:i},success:function(n){t($.map(n,function(n){return{label:n.cityId+", "+n.name+" ("+n.stateProvinceName+"-"+n.stateProvinceCode+")",value:n.cityId}}))}})},appendTo:"#result",select:function(n,t){$("#Address_CityId").val(t.item?t.item.value:"");$("#CityIdF").val(t.item?t.item.label:"")},change:function(n,t){$("#Address_CityId").val(t.item?t.item.value:"");$("#CityIdF").val(t.item?t.item.label:"")}})}RemoveAddress(n){var t=$(n).parent().parent();$.ajax({dataType:"json",type:"POST",url:"/Address/MarkDelete",data:{id:$(n).attr("data-id")},success:function(){t.slideUp(500,function(){t.remove()})}})}Makeditable(n){$(n).editable({url:"/Address/PostInfo",success:function(n,t){$.smallBox({title:"Change",content:"<i class='fa fa-clock-o'><\/i> <i> 1 seconds ago...<\/i>"+t,color:"#296191",iconSmall:"fa fa-thumbs-up bounce animated",timeout:4e3})},error:function(){$.smallBox({title:"Error",content:"<i class='fa fa-clock-o'><\/i> <i>1 seconds ago...<\/i>",color:"#ff6a00",iconSmall:"fa fa-thumbs-down bounce animated",timeout:4e3})}})}}var address=new Address;